mapper.select:
select like_user
from board
where bno = #{bno}

select *
from (
  select bno
  from board
  where is_del = 0 and reg_date > #{date} and like_user like '#%'
  group by bno
  order by reg_date desc
  limit #{pageStart}, #{qty}
) a
left join board b on a.bno = b.bno

mapper.update:
update board
set like_user = #{likeUser}
where bno
= #{bno}

mapper.select:
select *
	from (
	select bno
	from board
	where is_del = 0 and
	id=#{keyword}
	group by bno
	order by bno desc
	limit #{pageStart}, #{qty}
	) a
	left join
	board b on a.bno = b.bno
